mapper.select:
  		select * from (
		select rownum R, N.* from  		
		(
  			select * from wish  where id=#{id}
  			select W.* from wish W, movie M where W.id=#{id} and W.movie_num=M.movie_num order by M.open_date desc
  			select * from wish  where id=#{id}
  		) N)
		where R BETWEEN #{rowNum.startRow} and #{rowNum.lastRow}
